// Now build 500 prompts distributed across 31 categories
function buildAllPrompts() {
  const allPrompts = [];
  let promptIndex = 0;
  
  // For each category, we need ~16 prompts
  // 10 nature, 4 abstract, 2 other
  // Distribute moods across each category's 16 prompts (2 each of 8 moods)
  
  const moodCycle = ['calm','contemplative','mystical','warm','hopeful','grounding','intimate','ethereal'];
  
  // Category-specific mood-to-scene mapping for better thematic matching
  const categoryMoodHints = {
    grief_loss: { preferred: ['contemplative','intimate','calm','ethereal'], scenes: ['rain','autumn','candle','fog','willow'] },
    morning_power: { preferred: ['hopeful','warm','grounding','ethereal'], scenes: ['sunrise','dew','mist_lifting','golden'] },
    stress_relief: { preferred: ['calm','grounding','intimate','ethereal'], scenes: ['ocean','waves','rain','breathing','snow'] },
    creativity: { preferred: ['mystical','ethereal','hopeful','warm'], scenes: ['ink','prismatic','watercolor','paint'] },
    evening_reflection: { preferred: ['contemplative','intimate','calm','mystical'], scenes: ['sunset','moonlit','twilight','candle'] },
    rock_bottom_recovery: { preferred: ['hopeful','grounding','warm','ethereal'], scenes: ['seedling','sunrise','frost_melting','rain_dry_earth'] },
    loneliness: { preferred: ['intimate','contemplative','warm','ethereal'], scenes: ['candle','rain_window','moonlit','fog'] },
    healing: { preferred: ['warm','calm','ethereal','grounding'], scenes: ['hot_spring','waterfall','moss','gentle_stream'] },
    transformation: { preferred: ['hopeful','mystical','ethereal','warm'], scenes: ['frost_melting','fern_unfurling','butterfly','embers'] },
    self_belief: { preferred: ['hopeful','warm','grounding','ethereal'], scenes: ['mountain','eagle','oak','river'] },
    courage: { preferred: ['warm','hopeful','grounding','mystical'], scenes: ['mountain','sunrise','ocean','thunder'] },
    resilience: { preferred: ['grounding','hopeful','warm','calm'], scenes: ['river_stones','mountain','bamboo','wildflower'] },
    focus: { preferred: ['calm','grounding','contemplative','intimate'], scenes: ['rain','stream','zen','minimal'] },
    gratitude: { preferred: ['warm','hopeful','intimate','ethereal'], scenes: ['golden_hour','sunrise','flowers','dew'] },
  };
  
  for (let catIdx = 0; catIdx < categories.length; catIdx++) {
    const category = categories[catIdx];
    const promptsForCat = [];
    
    // Pick 10 nature, 4 abstract, 2 other
    // Rotate through the pools based on category index to avoid repeats
    const natureOffset = (catIdx * 10) % naturePrompts.length;
    const abstractOffset = (catIdx * 4) % abstractPrompts.length;
    const otherOffset = (catIdx * 2) % otherPrompts.length;
    
    for (let i = 0; i < 10; i++) {
      const srcIdx = (natureOffset + i) % naturePrompts.length;
      const src = naturePrompts[srcIdx];
      const mood = moodCycle[(i) % moodCycle.length];
      promptsForCat.push({
        ...src,
        mood,
        style: 'nature',
        category,
      });
    }
    
    for (let i = 0; i < 4; i++) {
      const srcIdx = (abstractOffset + i) % abstractPrompts.length;
      const src = abstractPrompts[srcIdx];
      const mood = moodCycle[(10 + i) % moodCycle.length];
      promptsForCat.push({
        ...src,
        mood,
        style: 'abstract',
        category,
      });
    }
    
    for (let i = 0; i < 2; i++) {
      const srcIdx = (otherOffset + i) % otherPrompts.length;
      const src = otherPrompts[srcIdx];
      const mood = moodCycle[(14 + i) % moodCycle.length];
      promptsForCat.push({
        ...src,
        mood,
        style: i === 0 ? 'cosmic' : 'minimal',
        category,
      });
    }
    
    allPrompts.push(...promptsForCat);
  }
  
  // 31 * 16 = 496, need 4 more
  // Add 4 universal extras
  const extras = [
    { ...naturePrompts[0], mood: 'calm', style: 'nature', category: 'universal' },
    { ...naturePrompts[5], mood: 'ethereal', style: 'nature', category: 'universal' },
    { ...abstractPrompts[0], mood: 'warm', style: 'abstract', category: 'universal' },
    { ...otherPrompts[0], mood: 'mystical', style: 'cosmic', category: 'universal' },
  ];
  allPrompts.push(...extras);
  
  return allPrompts;
}

async function main() {
  console.log('Building 500 prompts...');
  const allPrompts = buildAllPrompts();
  console.log(`Generated ${allPrompts.length} prompts`);
  
  // Transform to DB format
  const rows = allPrompts.map((p, idx) => ({
    title: p.title,
    prompt: p.prompt,
    category: p.category,
    subcategory: p.sub,
    mood: p.mood,
    style: p.style,
    motion_type: p.motion || 'slow_drift',
    format: '9:16 vertical',
    loop_friendly: true,
    caption_safe: true,
    platform: 'higgsfield',
    status: 'draft',
    tags: p.tags || [],
  }));
  
  // Insert in batches of 50
  const batchSize = 50;
  let inserted = 0;
  
  for (let i = 0; i < rows.length; i += batchSize) {
    const batch = rows.slice(i, i + batchSize);
    const { data, error } = await supabase.from('background_prompts').insert(batch);
    if (error) {
      console.error(`Error inserting batch ${i / batchSize + 1}:`, error.message);
      // Try one by one
      for (const row of batch) {
        const { error: e2 } = await supabase.from('background_prompts').insert([row]);
        if (e2) console.error(`  Failed row "${row.title}":`, e2.message);
        else inserted++;
      }
    } else {
      inserted += batch.length;
      console.log(`Inserted batch ${Math.floor(i / batchSize) + 1}/${Math.ceil(rows.length / batchSize)} (${inserted} total)`);
    }
  }
  
  console.log(`\nInserted ${inserted} new rows`);
  
  // Count total
  const { count, error: countErr } = await supabase.from('background_prompts').select('*', { count: 'exact', head: true });
  if (countErr) console.error('Count error:', countErr.message);
  else console.log(`Total rows in background_prompts: ${count}`);
}

main().catch(console.error);
